<?php/* * We need to figure out what exactly the OrderNow link should do */$orderNowHref = null;$orderNowClass = null;//Michael Moore 12/15/2015//$OnlineOnly = -1; // going to use this to decide if the 'Order Now' button should appear//First, let's see if they have selected a locationif(isset($_SESSION['hasSelected']) && isset($_SESSION['selectedLocation']) && $_SESSION['hasSelected'] ){    //Ok, so they have selected a location    //first, check if the location is web_only.    if($locationData[$_SESSION['selectedLocation']]['web_only'] == 0){ // if location is not online only       $OnlineOnly = 1; // set it to false    }else{ // location is online only       $OnlineOnly = 0; // the location is online only, therefore, all locations are online only    }    //Now we check to see if the selected location has delivery or takeout or both    $selectedLocation = $_SESSION['selectedLocation'];    if(intval($locationData[$selectedLocation]['deliveryEnabled']) == 1 && intval($locationData[$selectedLocation]['takeoutEnabled']) == 1){        //This location has both enabled, so they need to pick what they want        $orderNowClass = "ajaxOrderNowLink";        $orderNowHref = "selectmethod_pop.php";    }else{        //It only has one, we won't waste a colorbox call on this        $orderNowClass = "orderNowLink";        $orderNowHref = "select_method.php";    }}else{    //So they haven't selected a location yet, is there more than one location?    if(count($locationData) > 1){        //NMore than one location and they haven't chosen yet?  Well lets do that first        $orderNowClass = "orderNowLink";        $orderNowHref = "location.php";    }else{        //only one location? Well, they don't have a choice so we will use that one, does it have takeout, delivery or both?        $selectedLocation = $_SESSION['selectedLocation'];        //first, check if the location is web_only.        if($locationData[$_SESSION['selectedLocation']]['web_only'] == 0){ // if location is not online only            $OnlineOnly = 1; // set it to false        }else{ // location is online only            $OnlineOnly = 0; // the location is online only, therefore, all locations are online only        }        if(intval($locationData[$selectedLocation]['deliveryEnabled']) == 1 && intval($locationData[$selectedLocation]['takeoutEnabled']) == 1){            //This location has both enabled, so they need to pick what they want            $orderNowClass = "ajaxOrderNowLink";            $orderNowHref = "selectmethod_pop.php";        }else{            //It only has one, we won't waste a colorbox call on this            $orderNowClass = "orderNowLink";            $orderNowHref = "select_method.php";        }    }}if (intval($locationData[$selectedLocation]['online_only']) == 1){    $hiddenStyle = "style='display:hidden'";}if(isset($_SESSION['cart']) && isset($_SESSION['cart']->items)){    $cartCount = count($_SESSION['cart']->items);}else{    $cartCount = 0;}echo "<ul class='topnav' id='myTopnav'>";if (intval($locationData[$_SESSION['selectedLocation']]['webOnly']) == 0 ){    //echo " the count right now is :  "  . count($_SESSION['cart']->items);    if( count($_SESSION['cart']->items) > 0) {echo "<li class='right'><a id='cartPop' href='ajaxcart.php'>My Order <div id='cartCount'>{$cartCount}</div></a></li>";} // if there are items in cart... display 'Checkout' button    else{        if($siteSettings['onlineOrdering']){            echo "<li><a class='$orderNowClass' href='$orderNowHref'>Order Now</a></li>";        }    }}if (intval($locationData[$selectedLocation]['online_only']) != 1){    /*    $siteSettings['starterSite']    $siteSettings['onlineOrdering']    $siteSettings['allowLoyalty']    */    if($siteSettings['onlineOrdering']){        echo "<li><a href='select_items.php'>Menu</a></li>";    }    if($siteSettings['starterSite']){        echo "<li $hiddenStyle><a href='index.php'>Restaurant</a></li>";        if( !($_SESSION['selectedLocation'] == 47) && !($_SESSION['selectedLocation'] == 46)  ) // this is for removing the 'about us' page from websites that don't want it. This will be replaced by a DB setting soon        {            echo "<li $hiddenStyle><a href='aboutus.php'>About Us</a></li>";        }        if(count($locationData) > 1){            echo "<li><a href='location.php'>Location"; if(count($locationData) > 1) { echo "s"; } echo "</a></li>";        }    }}if($gcUser->valid){ // Logged in    //if (Not a facebook account)    if(!isset($_SESSION['fbuid']) || $_SESSION['fbuid'] == "" || $_SESSION['fbuid'] == "0"){        $fb = false;    }    else{        $getPicture = new QueryCust("SELECT picture FROM customers_facebook WHERE userID = ".$gcUser->userID, __LINE__, __FILE__);        if ($getPicture->numrows() != 0) {            $fbPictureURL = $getPicture->result(0, "picture");            $fb = true;        }    }    // Making room for Loyalty    //<li class='right'><a href='myaccount.php'>My Account</a></li>    //<li class='right'><a href='myaccount.php' ".($fb?'style="padding: 12px 16px 8px 16px;"':'').">".($fb?'<img style="height: 29px; vertical-align:middle;" src="'.$fbPictureURL.'">':'')." Hello, {$gcUser->fname}</a></li>    echo "        <li class='right'><a href='logout.php'>Logout</a></li>        <li class='right'><a href='myaccount.php'>My Account</a></li>    ";    if($siteSettings['allowLoyalty']){        echo "<li class='right'><a href='loyalty.php'>Loyalty</a></li>";    }    if($siteSettings['allowPrevOrders']) {        echo "<li class='right'><a class='prevOrderPop' href='prev_order_pop.php'>Previous Orders</a></li>";    }} else { // if not logged in to the site    echo "<li class='right'><a id='loginPop' href='login_pop.php'>Login</a></li>";    if($siteSettings['allowLoyalty']){        echo "<li class='right'><a href='loyalty.php'>Loyalty</a></li>";    }}?><li class="icon">    <a href="javascript:void(0);" style="font-size:15px;" onclick="myFunction()">â˜°</a></li></ul>